       user: null,
       playlists: [],
       playing: false,
       item: null,
};
//state is what we currenly have, the action sets the state to what we want, like playing to true or playing to false
const reducer = (state, action) =>  {
       console.log(action);
       switch (action.type) {
              case 'SET_USER':
                     return {
                            ...state, 
                            user: action.user
                     }
              case 'SET_PLAYLISTS':
                     return {
                            ...state,
                            playlists: action.playlists
                     }
              case 'SET_PLAYING':
